function hideElement(e){e.classList.add("hidden")}function showElement(e){e.classList.remove("hidden")}function getBackImg(e){return e.split("_").map(e=>"front.png"===e?"back.png":e).join("_")}function getParameterList(e){let t="";for(var[r,n]of Object.entries(e))t+=`<li><p>${r}</p><p>${n}</p></li>`;return t}function resetQuantity(e){setSum(e.value=1)}function resetTextInput(e){e.value=""}function setSum(e){var t=document.querySelector(".section.side .productDescription .price").textContent.split(" ")[0];document.querySelector(".quantityAndSum .price").innerHTML=`Total: ${e*t} ₴`}function validateName(e,t){t=document.querySelector(t);return""===e?showError(t,"Enter your name!"):/[`0-9!@#$%^&*()_+=\[\]{};':"\\|,.<>\/?~]/.test(e)?showError(t,"No special characters are allowed in the name!"):e.trim().split(" ").length<2?showError(t,"Full name should contain at least 2 words!"):(t.innerHTML="",hideElement(t),1)}function validatePhone(e,t){t=document.querySelector(t);return""===e?showError(t,"Enter your phone number!"):e.length<7?showError(t,"Phone number must contain at least 7 numbers"):isNaN(e)||"."===(e=e.split(""))[0]||"-"===e[0]?showError(t,'Phone number could contain only numbers (and "+" at the beginning)!'):(t.innerHTML="",hideElement(t),1)}function validateEmail(e,t){t=document.querySelector(t);return""===e?showError(t,"Enter your email!"):/[`а-яА-Я#()\[\]{};:"\\|,<>\/]/.test(e)?showError(t,"Some of these characters are not allowed in the email!"):(e=e.split("@")).length<2?showError(t,'Email must contain "@" !'):e.includes("")?showError(t,"This is not an email!"):(t.innerHTML="",hideElement(t),1)}function validateCity(e,t){t=document.querySelector(t);return""===e?showError(t,"Select your city!"):(t.innerHTML="",hideElement(t),1)}function validateDepartment(e,t){t=document.querySelector(t);return""===e?showError(t,"Select the Nova Post department!"):(t.innerHTML="",hideElement(t),1)}function showError(e,t){return e.innerHTML=t,showElement(e),0}async function submitForm(e){console.log(e)}function formatDate(e){return String(e.getDate()).padStart(2,"0")+`.${String(e.getMonth()+1).padStart(2,"0")}.`+String(e.getFullYear()).padStart(2,"0")}function formatTime(e){return String(e.getHours()).padStart(2,"0")+`:${String(e.getMinutes()).padStart(2,"0")}:`+String(e.getSeconds()).padStart(2,"0")}function toggleCategories(e,t){t=document.querySelector(t);"My orders"===e?hideElement(t):"Categories"===e&&showElement(t)}function renderOrderDetails(e,t,r,n){e.innerHTML=n?`<div class='orderDetails'>
        <img src='${r.img}'>
        <div class='name-n-price'> 
            <h3><span class='quantitySpan'>x${t.quantity}</span> ${r.name}</h3>
            <p class="price">${t.price*t.quantity} ₴</p>
        </div>
        <div class='date-n-time'>
            <p class='date'>${t.date}</p>
            <p class='time'>${t.time}</p>
        </div>
        <p class='del' id='del_${t.orderID}'>Delete</p>
        </div>`:`<div class='orderDetails'>
        <h2>Thanks for the order!</h2>
        <img src='${r.img}'>
        <div class='name-n-price'> 
        <h3><span class='quantitySpan'>x${t.quantity}</span> ${r.name}</h3>
            <p class="price">${t.price*t.quantity} ₴</p>
        </div>
        <div class='date-n-time'>
            <p class='date'>${t.date}</p>
            <p class='time'>${t.time}</p>
        </div>
        </div>`}export{hideElement,showElement,getBackImg,getParameterList,resetQuantity,resetTextInput,setSum,validateName,validatePhone,validateEmail,validateCity,validateDepartment,showError,submitForm,formatDate,formatTime,toggleCategories,renderOrderDetails};